ALTER TABLE public.activities ADD CONSTRAINT activities_user_id_fkey FOREIGN KEY (user_id) REFERENCES auth.users(id);


CREATE TABLE public.records (
	id serial4 NOT NULL,
	"time_stamp" timestamptz NULL,
	"position" point NULL,
	altitude int4 NULL,
	heart_rate int2 NULL,
	cadence int2 NULL,
	distance int4 NULL,
	speed int4 NULL,
	temperature int2 NULL,
	gps_accuracy int2 NULL,
	enhanced_altitude int4 NULL,
	activity_id int4 NULL,
	CONSTRAINT records_pkey PRIMARY KEY (id)
);


CREATE VIEW activity_with_records_view AS 
SELECT 
    a.id,
    a.created_at,
    a.user_id,
    a.distance,
    a.activity_name,
    a.avg_speed,
    a.max_speed,
    a.ride_type,
    a.elapsed_time,
    a.total_time,
    TO_CHAR(a.elapsed_time::time, 'HH24:MI:SS') AS elapsed_time_char,
    TO_CHAR(a.total_time::time, 'HH24:MI:SS') AS total_time_char,
    JSON_AGG(r.* ORDER BY r.time_stamp) AS records
FROM activities a
JOIN records r ON r.activity_id = a.id
GROUP BY 
    a.id, 
    a.created_at,
    a.user_id,
    a.distance,
    a.activity_name,
    a.avg_speed,
    a.max_speed,
    a.ride_type,
    a.elapsed_time,
    a.total_time;
